const initialState = {
    countries: [],
    allCountries: [],
    Activities: [],
    countriesBackup: [],
}


function rootReducer(state = initialState, action) {
    switch (action.type) {
        case `GET_COUNTRIES`:
            return {
                ...state,
                countries: action.payload,
                allCountries: action.payload
            }
        case 'FILTER_BY_CONTINENT':
            const allCountries = state.allCountries
            const continentFiltered = action.payload === 'All' ? allCountries :
                allCountries.filter(el => el.continent === action.payload)
            return {
                ...state,
                countries: continentFiltered
            }

        case 'ORDER_BY_NAME':
            action.payload === "asc" ? state.countries.sort(function (a, b) {
                if (a.name > b.name) {
                    return 1
                }
                if (b.name > a.name) {
                    return -1
                }
                return 0
            }) :
                state.countries.sort(function (a, b) {
                    if (a.name > b.name) {
                        return -1
                    }
                    if (b.name > a.name) {
                        return 1
                    }
                    return 0
                })
            return {
                ...state,
                countries: state.countries

            }

            case 'ORDER_BY_POPULATION':
            action.payload === "min" ? state.countries.sort(function (a, b) {
                if (a.population > b.population) {
                    return 1
                }
                if (b.population > a.population) {
                    return -1
                }
                return 0
            }) :
                state.countries.sort(function (a, b) {
                    if (a.population > b.population) {
                        return -1
                    }
                    if (b.population > a.population) {
                        return 1
                    }
                    return 0
                })
            return {
                ...state,
                countries: state.countries

            }

        case 'GET_NAME_COUNTRIES':
            return {
                ...state,
                countries: action.payload
            }

        case 'CREATE_NEW_ACTIVITY':
            return {
                ...state,
                newActivity: action.payload,
            };
        case 'GET_ACTIVITIES':
            //console.log(action.payload)
            return {
                ...state,
                activities: action.payload,
            }    

            case 'FILTER_ACTIVITY':
                const filtredCountriesByActivities = state.countriesBackup
                const continentFilteredBA = filtredCountriesByActivities.filter((c) => { return c.activities.find((c) => { return c.name === action.payload; }); });
                const filterAll=filtredCountriesByActivities.filter((c) => { return c.activities.length>0; });
                if (action.payload === 'All') {
                    return { ...state, allCountries: filterAll }
                } else {
                    return {
                        ...state,
                        allCountries: continentFilteredBA
                    }
                }
        default:
            return state;
    }
}

export default rootReducer;
