import {Fragment, React} from "react";
import {/*useState*/ useEffect} from "react";
import {useDispatch, useSelector} from 'react-redux'
import { getCountries } from "../actions";
import {Link} from "react-router-dom"
import Card from './Card'

export default function Home(){

    const dispatch = useDispatch
    const allCountries = useSelector ((state) => state.countries)

    useEffect (()=>{
        dispatch(getCountries());
    },[dispatch])

    function handleClick(e){
        e.preventDefault();
        dispatch(getCountries());
    }


    return (
        <div>
            <Link to='/countries'>crear...</Link>
            <h1>Vemos q ponemos</h1>
            <button onClick={e=> {handleClick(e)}}>
                volver a cargar todos los paises</button>
            <div>
                <select>
                    <option value= 'asc'>Ascendente</option>
                    <option value= 'desc'>Descendente</option>
                </select>
                <select>
                    <option value='todos'>Todos</option>
                    <option value='asia'>Asia</option>
                    <option value='america'>América</option>
                    <option value='africa'>África</option>
                    <option value='antartida'>Antartida</option>
                    <option value='europa'>Europa</option>
                    <option value='oceania'>Oceania</option>
                </select>
                <select>
                    <option value= 'actividades'>Actividades</option>
                </select>
                    {allCountries?.map((c)=>{
                        return (
                            <Fragment>
                                <Link to = {"/countries/" + c.id}>
                                    <Card name= {c.name} image={c.img} continent={c.continent} key={c.id}/>
                                </Link>
                            </Fragment>
                        )
                    })}

                
                {/* {
                    allCountries && allCountries.map( el=>{
                        <Card name={el.name} image={el.img} continent={el.continent}/>
                    })} */}
            </div>
        </div>
    )
}
