import axios from 'axios';

export function getCountries() {
    return async function (dispatch) {
        var json = await axios("http://localhost:3001/countries");
        return dispatch({
            type: 'GET_COUNTRIES',
            payload: json.data
        })
    }
}

export function addActivity(body) {
    return async function (dispatch) {
        try {
            //console.log(body)  
            var activity = await axios.post(`http://localhost:3001/activities`, body);
            return dispatch({
                type: 'POST_ACTIVITY',
                payload: activity.data
            })
        } catch (error) {
            console.log(error)
        }
    }
}
export function getActivities() {
    return async function (dispatch) {
      var info = await axios.get("http://localhost:3001/activity", {});
      return dispatch({
        type: "GET_ACTIVITIES",
        payload: info.data,  //puedo ponerle info o json
      });
    };
  }

export function getNameCountry(name) {
    return async function (dispatch) {
        var json = await axios("http://localhost:3001/countries?name=" + name);
        return dispatch({
            type: "GET_NAME_COUNTRIES",
            payload: json.data
        })
    }
}

export function filterCountryByContinent(payload){
    return{
        type: 'FILTER_BY_CONTINENT',
        payload
    }
}

export function orderByName(payload){
    return{

        type: 'ORDER_BY_NAME',
        payload
    }
}

export function orderByPopulation(payload){
    return{

        type: 'ORDER_BY_POPULATION',
        payload
    }
}

export function filterActivity(payload) {
    //console.log('the activity is',payload);
    return {
        type: 'FILTER_ACTIVITY',
        payload
    }
}
