const initialState = {
    countries : [],
    allCountries
}


function rootReducer (state =initialState, action){
    switch(action.type){
        case `GET_COUNTRIES`:
            return{
                ...state,
                countries: action.payload
            }
            case 'FILTER_BY_STATUS':
                const allCountries = state.countries
                const statusFiltered = action.payload === 'All' ? allCountries :
                allCountries.filter(el => el.status === action.payload)
                return{
                    ...state,
                    countries: statusFiltered
                }

            default:
                return state;
    }
}

export default rootReducer;
