import React, {useState,useEffect} from "react";
import { Link, useHistory } from "react-router-dom";
import { postActivities, getCountries } from "../actions";
import { useDispatch, useSelector } from "react-redux";


export default function ActivityCreate(){
    const dispatch = useDispatch()
    const activities = useSelector((state) => state.activities)

    const [input, setInput] = useState({
        name: "",
        difficulty: "",
        duration: "",
        season: [],
        countries: [], //lo seteo en un array para tener la posibilidad de poner mÃ¡s de una
        id: [],
    });

    function handleChange(e){
        setInput({
            [e.target.name] : e.target.value
        })
        console.log(input)
    }

    function handleCheck(e){
        if (e.target.checked){
            setImput({
                ...input,
                status: e.target.value
            })
        }
    }

    function handleSelectCountries(e){
        setInput({
            ...input,
            activities: [...input.activities,e.target.value]
        })
    }

    useEffect(() => {
        dispatch(getActivities())
    }, []);
    return(
    <div className={s.bodyActivityCreate}>
        {/* <div>
        <NavBar />
      </div> */}
        {/* //hago esto por si me arrepiento y quiero volver a home */}
        {/* <Link to="/home">
        <button className={s.btnVolAct}>Volver</button>
      </Link> */}
        {/* todo lo que se envuelva en un link va a funcionar como redirecciÃ³n */}
        {/* <div className="container"> */}
        <div className={s.drop1}></div>
        <div className={s.drop2}></div>
        <div className={s.drop5}></div>

        <div className={s.cardForm}>
            <h1>Crear actividad turÃ­stica</h1>

            <form onSubmit={(e) => handleSubmit(e)}>
                <div className={s.inputgroup}>
                    <label className={s.alllabel}>Nombre:</label>
                    <br></br>
                    <input
                        type="text"
                        name="name"
                        value={input.name}
                        onChange={(e) => handleChange(e)}
                        placeholder="--Nombre de la actividad--"
                        className={s.input1}
                        required
                    />

                    {errors.name && ( //si existe un error en el nombre, entonces renderizame un p que diga el error
                        <p>{errors.name}</p>
                    )}
                </div>
                <br></br>
                <div>
                    <label className={s.alllabel}>Dificultad (1-5):</label>
                    <br></br>
                    <div className={s.divradio}>
                        <input
                            type="range"
                            id="get"
                            name="difficulty"
                            value={input.difficulty}
                            min="1"
                            max="5"
                            step="1"
                            onChange={(e) => handleChange(e)}
                            className={s.input2}
                            required
                            // value="1"
                            list="difficulty"
                        />
                        <p>{input.difficulty}</p>
                        <datalist id="difficulty">
                            <option value="1"></option>
                            <option value="2"></option>
                            <option value="3"></option>
                            <option value="4"></option>
                            <option value="5"></option>
                        </datalist>
                        {/* <input type="text" id="put" /> */}
                    </div>

                    {errors.difficulty && ( //si existe un error en el nombre, entonces renderizame un p que diga el error
                        <p>{errors.difficulty}</p>
                    )}
                </div>
                <br></br>
                <div>
                    <label className={s.alllabel}>DuraciÃ³n:</label>
                    <br></br>
                    <input
                        type="time"
                        name="duration"
                        value={input.duration}
                        onChange={(e) => handleChange(e)}
                        placeholder="--DuraciÃ³n en horas--"
                        className={s.input1}
                        required
                    />
                    {errors.duration && ( //si existe un error en el nombre, entonces renderizame un p que diga el error
                        <p>{errors.duration}</p>
                    )}
                </div>
                <br></br>
                <div>
                    <label className={s.alllabel}>Temporada / s:</label>
                    <fieldset className={s.divradio}>
                        {/* <legend>Temporada:</legend> */}
                        {/* <label>Temporada:</label> */}
                        <label>
                            <input
                                type="checkbox"
                                name="seasonv"
                                value="Verano"
                                onChange={(e) => handleCheck(e)}
                            // required
                            />
                            Verano
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                name="seasono"
                                value="OtoÃ±o"
                                onChange={(e) => handleCheck(e)}
                            // required
                            />
                            OtoÃ±o
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                name="seasoni"
                                value="Invierno"
                                onChange={(e) => handleCheck(e)}
                            // required
                            />
                            Invierno
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                name="seasonp"
                                value="Primavera"
                                onChange={(e) => handleCheck(e)}
                            // required
                            />
                            Primavera
                        </label>

                        {/* <select onChange={(e) => handleChangeSeason(e)} required> */}
                        {/* <option value="" disable selected hidden> Seleccionar una o mÃ¡s temporadas</option>  */}
                        {/* hidden -> oculta la opciÃ³n */}
                        {/* {season.map((s) => (
                <option value={s} name="season">{s}</option>
              ))} */}

                        {/* </select> */}
                        {/* <ul>
            {input.season.map((s) => (
                <li>{s}</li>
              ))} */}
                        {/* âlista que va a tomar mi estado input.countries, y me va a renderizar cada cada cosa que vaya marcando en el select/ para ver lo que voy seleccionando */}
                        {/* </ul> */}
                    </fieldset>
                    {errors.season && ( //si existe un error en el nombre, entonces renderizame un p que diga el error
                        <p>{errors.season}</p>
                    )}
                </div>
                <br></br>

                <div>
                    <label className={s.alllabel}>Paises:</label>
                    <br></br>
                    <select
                        className={s.divpaises}
                        onChange={(e) => handleSelectCountries(e)}
                    // required
                    >
                        <option value={""} disable selected hidden>
                            {" "}
                            --Seleccionar uno o mÃ¡s paÃ­ses--
                        </option>
                        {countries.map((country) => (
                            <option value={JSON.stringify(country)}>{country.name}</option>
                        ))}
                    </select>

                    <ul className={s.ulchips}>
                        {input.countries.map((c) => (
                            <div className={s.chip_content}>
                                <img
                                    src={c.flags}
                                    alt="flag"
                                    height="25px"
                                    width="25px"
                                    className={s.chipimg}
                                />

                                <p className={s.pchip}>{c.name}</p>
                                <div className={s.delete}>
                                    <span onClick={() => handleDelete(c)}>x</span>
                                </div>
                            </div>
                        ))}
                        {/* âlista que va a tomar mi estado input.countries, y me va a renderizar cada cada cosa que vaya marcando en el select/ para ver lo que voy seleccionando */}
                    </ul>
                    {errors.countries && ( //si existe un error en el nombre, entonces renderizame un p que diga el error
                        <p>{errors.countries}</p>
                    )}
                </div>

                <div>
                    <button className={s.crear} type="submit" disabled={thereAreErrors}>
                        {" "}
                        Crear
                    </button>
                </div>
            </form>
        </div>
        <div className={s.drop3}></div>
        <div class={s.drop4}></div>
        <div class={s.drop6}></div>

        {/* </div> */}
    </div>
  );

}
