import React, {useState,useEffect} from "react";
import { Link, useHistory } from "react-router-dom";
import { postActivities, getActivities } from "../actions";
import { useDispatch, useSelector } from "react-redux";


export default function ActivityCreate(){
    const dispatch = useDispatch()
    const activities = useSelector((state) => state.activities)

    const [input, setInput] = useState({
        name: "",
        difficulty: "",
        duration: "",
        season: [],
        countries: [], //lo seteo en un array para tener la posibilidad de poner más de una
        
    });

    function handleChange(e){
        setInput({
            [e.target.name] : e.target.value
        })
        console.log(input)
    }

    function handleCheck(e){
        if (e.target.checked){
            setInput({
                ...input,
                status: e.target.value
            })
        }
    }

    function handleSelectCountries(e){
        setInput({
            ...input,
            activities: [...input.activities,e.target.value]
        })
    }

    useEffect(() => {
        dispatch(getActivities())
    }, []);

    return(
        <div>
            <Link to='/countries'><button>volver</button></Link>
            <h1>Crea tu Actividad!!!</h1>
            <form>
                <div>
                    <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name= "name"/>
                </div>
                <div>
                <label>Dificultad:</label>
                    <input
                    type="text"
                    value={input.difficulty}
                    name= "difficulty"/>
                </div>
                
            </form>
        </div>
    )
    
}
