const initialState = {
    countries : [],
    allCountries: [],
    Activities: []
}


function rootReducer (state =initialState, action){
    switch(action.type){
        case `GET_COUNTRIES`:
            return{
                ...state,
                countries: action.payload,
                allCountries: action.payload
            }
            case 'FILTER_BY_CONTINENT':
                const allCountries = state.allCountries
                const continentFiltered = action.payload === 'All' ? allCountries :
                allCountries.filter(el => el.continent === action.payload)
                return{
                    ...state,
                    countries: continentFiltered
                }
                case "FILTER_BY_ACTIVITY":
                action.payload === 'All' ? state.allCountries = state.copyCountries.filter(info => info.Activities.length) :
                state.allCountries = state.copyCountries.filter(name => name.Activities.find((element) => element.name?.toLowerCase() === action.payload))
            return {
                ...state,
                allCountries: state.allCountries
            }

            default:
                return state;
    }
}

export default rootReducer;
