import React, {useState,useEffect} from "react";
import { Link, useHistory } from "react-router-dom";
import { postActivities, getActivities } from "../actions";
import { useDispatch, useSelector } from "react-redux";


export default function ActivityCreate(){
    const dispatch = useDispatch()
    const countries = useSelector((state) => state.countries)

    const [input, setInput] = useState({
        name: "",
        difficulty: "",
        duration: "",
        season: [],
        countries: [], //lo seteo en un array para tener la posibilidad de poner más de una
        
    });

    function handleChange(e){
        setInput({
            [e.target.name] : e.target.value
        })
        console.log(input)
    }

    function handleCheck(e){
        if (e.target.checked){
            setInput({
                ...input,
                status: e.target.value
            })
        }
    }

    function handleSelectCountries(e){
        setInput({
            ...input,
            activities: [...input.activities,e.target.value]
        })
    }

    function handleSelectCountries(e) {
        const selectCountry = JSON.parse(e.target.value);
        const val = input.countries.some(
            (country) => country.name === selectCountry.name
        );
        if (!val) {
            const newInput = {
                ...input,
                countries: [...input.countries, selectCountry],
                id: [...input.id, selectCountry.name],
            };
            setInput(newInput);
            setErrors(validate(newInput));
        }
        e.target.value = "";
    }
    console.log(input.id);

    useEffect(() => {
        dispatch(getActivities())
    }, []);

    return(
        <div>
            <Link to='/countries'><button>volver</button></Link>
            <h1>Crea tu Actividad!!!</h1>
            <form>
                <div>
                    <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name= "name"/>
                </div>
                <div>
                <label>Dificultad:</label>
                    <label><input
                    type="checkbox"
                    value="difficulty"
                    name= "1"/>1</label>
                    <label><input
                    type="checkbox"
                    value="difficulty"
                    name= "2"/>2</label>
                    <label><input
                    type="checkbox"
                    value="difficulty"
                    name= "3"/>3</label>
                    <label><input
                    type="checkbox"
                    value="difficulty"
                    name= "4"/>4</label>
                    <label><input
                    type="checkbox"
                    value="difficulty"
                    name= "5"/>5</label>
                </div>
                <div>
                <label>Duracion:</label>
                    <input
                    type="number"
                    value={input.duration}
                    name= "duration"/>
                </div>
                <div>
                <label>temporada:</label>
                    <input
                    type="number"
                    value={input.duration}
                    name= "duration"/>
                </div>
                <select>
                    {countries.map((c) => (
                        <option value={c.season}>{c.season}</option>
                    ))}
                </select>
            </form>
        </div>
    )
    
}
